&quot;; } close F;  PHP &lt;?php require_once($GLOBALS['g_campsiteDir']. &quot;/$ADMIN_DIR/country/common.php&quot;); require_once($GLOBALS['g_campsiteDir']. &quot;/classes/SimplePager.php&quot;); camp_load_translation_strings(&quot;api&quot;); $f_country_language_selected = camp_session_get('f_language_selected', ''); $f_country_offset = camp_session_get('f_country_offset', 0); if (empty($f_country_language_selected)) { $f_country_language_selected = null; } $ItemsPerPage = 20; $languages = Language::GetLanguages(null, null, null, array(), array(), true); $numCountries = Country::GetNumCountries($f_country_language_selected); $pager = new SimplePager($numCountries, $ItemsPerPage, &quot;index.php?&quot;); $crumbs = array(); $crumbs[] = array(getGS(&quot;Configure&quot;), &quot;&quot;); $crumbs[] = array(getGS(&quot;Countries&quot;), &quot;&quot;); echo camp_html_breadcrumbs($crumbs); ?&gt; &lt;?php if ($g_user-&gt;hasPermission(&quot;ManageCountries&quot;)) { ?&gt; &lt;table BORDER=&quot;0&quot; CELLSPACING=&quot;0&quot; CELLPADDING=&quot;1&quot;&gt; &lt;tr&gt; &lt;td&gt;&lt;a href=&quot;add.php&quot;&gt;&lt;?php putGS(&quot;Add new&quot;); ?&gt;&lt;/a&gt;&lt;/td&gt; &lt;/tr&gt; &lt;/table&gt; &lt;?php } ?&gt;  Python # Inline Python Comment class ExitNotifyThread(Thread): &quot;&quot;&quot; Block-Level Python Comment &quot;&quot;&quot; def run(self): global exitthreads, profiledir self.threadid = thread.get_ident() try: if not profiledir: # normal case Thread.run(self) else: try: import cProfile as profile except ImportError: import profile prof = profile.Profile() try: prof = prof.runctx(&quot;Thread.run(self)&quot;, globals(), locals()) except SystemExit: pass prof.dump_stats( \ profiledir + &quot;/&quot; + str(self.threadid) + &quot;_&quot; + \ self.getName() + &quot;.prof&quot;) except: self.setExitCause('EXCEPTION') if sys: self.setExitException(sys.exc_info()[1]) tb = traceback.format_exc() self.setExitStackTrace(tb) else: self.setExitCause('NORMAL') if not hasattr(self, 'exitmessage'): self.setExitMessage(None) if exitthreads: exitthreads.put(self, True) def setExitCause(self, cause): self.exitcause = cause def getExitCause(self): &quot;&quot;&quot;Returns the cause of the exit, one of: 'EXCEPTION' -- the thread aborted because of an exception 'NORMAL' -- normal termination.&quot;&quot;&quot; return self.exitcause def setExitException(self, exc): self.exitexception = exc def getExitException(self): &quot;&quot;&quot;If getExitCause() is 'EXCEPTION', holds the value from sys.exc_info()[1] for this exception.&quot;&quot;&quot; return self.exitexception def setExitStackTrace(self, st): self.exitstacktrace = st def getExitStackTrace(self): &quot;&quot;&quot;If getExitCause() is 'EXCEPTION', returns a string representing the stack trace for this exception.&quot;&quot;&quot; return self.exitstacktrace def setExitMessage(self, msg): &quot;&quot;&quot;Sets the exit message to be fetched by a subsequent call to getExitMessage. This message may be any object or type except None.&quot;&quot;&quot; self.exitmessage = msg def getExitMessage(self): &quot;&quot;&quot;For any exit cause, returns the message previously set by a call to setExitMessage(), or None if there was no such message set.&quot;&quot;&quot; return self.exitmessage  Ruby desc &quot;Edit a post (defaults to most recent)&quot; task :edit_post, :title do |t, args| args.with_defaults(:title =&gt; false) posts = Dir.glob(&quot;#{source_dir}/#{posts_dir}/*.*&quot;) post = (args.title) ? post = posts.keep_if {|post| post =~ /#{args.title}/}.last : posts.last if post puts &quot;Opening #{post} with #{editor}...&quot; system &quot;#{ENV['EDITOR']} #{post} &amp;&quot; else puts &quot;No posts were found with \&quot;#{args.title}\&quot; in the title.&quot; end end  SCSS @import &quot;grids&quot;; $blue: blue; a.myAnchor { color: $blue; &amp;:hover { text-decoration: underline; } &amp;:visited { color: purple; } } .error-input { @extend .input; border: 4px solid #e74c3c; } %input-style { font-size: 14px; } input { @extend %input-style; color: black; } @mixin media($queryString){ @media #{$queryString} { @content; } } @function getColumnWidth($width, $columns,$margin){ @return ($width / $columns) - ($margin * 2); } $settings: ( maxWidth: 800px, columns: 12, margin: 15px, breakpoints: ( xs: &quot;(max-width : 480px)&quot;, sm: &quot;(max-width : 768px) and (min-width: 481px)&quot;, md: &quot;(max-width : 1024px) and (min-width: 769px)&quot;, lg: &quot;(min-width : 1025px)&quot; ) );  SQL SELECT column_name, column_name FROM table_name;  XML &lt;employees&gt; &lt;employee&gt; &lt;firstName&gt;John&lt;/firstName&gt; &lt;lastName&gt;Doe&lt;/lastName&gt; &lt;/employee&gt; &lt;/employees&gt;  Source:
